mapper.update:

UPDATE mall.mall_tb_cart t
   SET t.modify_date = NOW(),
	   t.modify_user = #{modifyUser},
	   t.quantity = t.quantity + #{quantity}
 WHERE t.state = 'U'
   AND t.user_id = #{userId}
   AND t.shop_id = #{shopId}
   AND t.item_id = #{itemId}
   AND t.sku_id  = #{skuId}

mapper.select:

SELECT COUNT(1)
  FROM mall.mall_tb_cart t
 WHERE t.STATE = 'U'
   AND t.user_id = #{userId}
   AND t.shop_id = #{shopId}

SELECT t.cart_id AS cartId, t.shop_id AS shopId, t.item_id AS itemId, t.sku_id AS skuId, t.points_id AS pointsId, t.quantity FROM mall.mall_tb_cart t
, mdm.mdm_tb_block_shop p
WHERE t.state = 'U' AND t.user_id = #{userId}
AND t.shop_id = p.shop_id AND p.state = 'U' AND p.block_id = #{blockId}
AND t.shop_id = #{shopId}
AND t.item_id = #{itemId}
AND t.sku_id = #{skuId}
ORDER BY t.create_date DESC

