mapper.select:
SELECT t.sku_id AS skuId, t.item_id AS itemId, t.properties, t.properties_name AS propertiesName, t.stock, t.price, t.origin, t.code FROM dms.dms_tb_item_sku t WHERE t.state = 'U'
AND t.item_id = #{itemId}
AND t.sku_id IN #{code}
LIMIT ${limit} OFFSET ${offset}

mapper.update:

UPDATE dms.dms_tb_item_sku t
SET t.modify_date = NOW(),
  t.modify_user   = #{modifyUser},
  t.stock         = #{stock}
WHERE t.state     = 'U'
AND t.item_id     = #{itemId}
AND t.sku_id      = #{skuId}
