mapper.select:
SELECT t.item_id AS itemId, t.item_no AS itemNo, t.item_name AS itemName, t.price, t.stock, t.postage, t.quota, t.origin, t.remark FROM dms.dms_tb_item t WHERE t.state = 'U' AND t.shop_id = #{shopId}
AND t.item_id IN #{code}
ORDER BY t.item_name, t.item_id LIMIT ${limit} OFFSET ${offset}

update.foreach:

UPDATE dms.dms_tb_item t
SET t.modify_date = NOW(),
  t.modify_user   = #{2},
  t.stock         = #{item.stock}
WHERE t.state     = 'U'
AND t.shop_id     = #{0}
AND t.item_id     = #{item.itemId}
