mapper.select:

SELECT COUNT(1)
  FROM mall.mall_tb_trade t
 WHERE t.STATE = 'U'
   AND t.user_id = #{userId}


 AND t.type IN 
	#{code}

SELECT t.trade_id AS tradeId, t.trade_no AS tradeNo, t.shop_id AS shopId, t.type, t.trade_price AS tradePrice, t.coupon_price AS couponPrice, t.postage, t.change, t.trade_points AS tradePoints, t.receiver_name AS receiverName, t.receiver_tel AS receiverTel, DATE_FORMAT(t.create_date, '%Y-%m-%d %T') AS createDate, DATE_FORMAT(t.modify_date, '%Y-%m-%d %T') AS modifyDate, DATE_FORMAT(t.pay_date, '%Y-%m-%d %T') AS payDate, DATE_FORMAT(t.send_date, '%Y-%m-%d %T') AS sendDate, DATE_FORMAT(t.sign_date, '%Y-%m-%d %T') AS signDate, DATE_FORMAT(t.feedback_date, '%Y-%m-%d %T') AS feedbackDate, DATE_FORMAT(t.feedbacked_date, '%Y-%m-%d %T') AS feedbackedDate, IFNULL(t.score, 0), t.shipment, t.feedback_type AS feedbackType FROM mall.mall_tb_trade t WHERE t.state = 'U' AND t.user_id = #{userId}
AND t.type IN #{code}
ORDER BY TRADE_ID DESC
ORDER BY ${sort} ${order}, TRADE_ID DESC
LIMIT ${limit} OFFSET ${offset}

mapper.update:

UPDATE mall.mall_tb_trade t
SET t.modify_date = NOW(),
  t.modify_user   = #{modifyUser}


 , t.type = #{type} 



	 , t.PAY_DATE = STR_TO_DATE(#{payDate}, '%Y%m%d%H%i%s') 


	 , t.SEND_DATE = NOW() 


	 , t.SIGN_DATE = NOW() 


	 , t.FEEDBACK_DATE = NOW() 


	 , t.FEEDBACKED_DATE = NOW() 


	 , t.state = 'D' 



 , t.receiver_name = #{receiverName} 


 , t.receiver_province = #{receiverProvince} 


 , t.receiver_city = #{receiverCity} 


 , t.receiver_area = #{receiverArea} 


 , t.receiver_back_code = #{receiverBackCode} 


 , t.receiver_address = #{receiverAddress} 


 , t.receiver_zip = #{receiverZip} 


 , t.receiver_tel = #{receiverTel} 


 , t.receiver_remark = #{receiverRemark} 


 , t.shipment = #{shipment} 



 , t.deliver_date = #{deliverDate} 


 , t.deliver_start_time = #{deliverStartTime} 


 , t.deliver_end_time = #{deliverEndTime} 



 , t.pay_type = #{payType} 


 , t.feedback_type = #{feedbackType} 


 , t.score = #{score} 


WHERE t.state       = 'U'


 AND t.trade_id = #{tradeId} 


 AND t.trade_no = #{tradeNo} 


 AND t.user_id = #{userId} 


 AND t.type <> #{type}
